import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../assets/css/ResumeBuilder.css';

const ResumeBuilder = () => {
  const { templateId } = useParams();
  const navigate = useNavigate();
  
  const [currentStep, setCurrentStep] = useState(1);
  const [resumeData, setResumeData] = useState({
    personalInfo: {
      fullName: '',
      email: '',
      phone: '',
      address: '',
      linkedin: '',
      website: ''
    },
    summary: '',
    education: [
      { id: 1, institution: '', degree: '', fieldOfStudy: '', startDate: '', endDate: '', description: '' }
    ],
    experience: [
      { id: 1, company: '', position: '', location: '', startDate: '', endDate: '', description: '' }
    ],
    skills: ['']
  });
  
  return (
    <div className="resume-builder-page">
      <div className="page-header">
        <h1>Build Your Resume</h1>
        <p>Template: {templateId === '1' ? 'Professional' : templateId === '2' ? 'Creative' : 'Simple'}</p>
      </div>
      
      <div className="builder-container">
        <div className="steps-indicator">
          <div className={`step ${currentStep >= 1 ? 'active' : ''}`}>Personal Info</div>
          <div className={`step ${currentStep >= 2 ? 'active' : ''}`}>Experience</div>
          <div className={`step ${currentStep >= 3 ? 'active' : ''}`}>Education</div>
          <div className={`step ${currentStep >= 4 ? 'active' : ''}`}>Skills</div>
        </div>
        
        <div className="form-container">
          <div className="form-content">
            <h2>This is a placeholder for the resume builder form</h2>
            <p>In a complete implementation, this would include multi-step forms for:</p>
            <ul>
              <li>Personal Information</li>
              <li>Work Experience</li>
              <li>Education</li>
              <li>Skills</li>
            </ul>
            <p>And a preview of the selected template with the user's data.</p>
          </div>
          
          <div className="form-actions">
            <button 
              className="btn-secondary"
              onClick={() => navigate('/resume-templates')}
            >
              Back to Templates
            </button>
            <button 
              className="btn-primary"
              onClick={() => navigate('/profile')}
            >
              Save Resume
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResumeBuilder;
